mapper.select:

select * FROM  library.book_book bu where
1=1

    and bu.book_id =#{bookId,jdbcType=INTEGER}


    and bu.book_name  like CONCAT('%',#{bookName},'%')


    and bu.book_type_id =#{bookTypeId,jdbcType=INTEGER}



    and bu.book_author =#{bookAuthor,jdbcType=VARCHAR}


    and bu.ISBN =#{isbn,jdbcType=VARCHAR}


    and bu.book_author =#{bookAuthor,jdbcType=VARCHAR}


    and bu.is_valid =#{isValid,jdbcType=VARCHAR}

select count(*) FROM  library.book_user bu where
1=1

    and bu.book_id =#{bookId,jdbcType=INTEGER}


    and bu.book_name  like CONCAT('%',#{bookName},'%')


    and bu.book_type_id =#{bookTypeId,jdbcType=INTEGER}



    and bu.book_author =#{bookAuthor,jdbcType=VARCHAR}


    and bu.ISBN =#{isbn,jdbcType=VARCHAR}


    and bu.book_author =#{bookAuthor,jdbcType=VARCHAR}


    and bu.is_valid =#{isValid,jdbcType=VARCHAR}

select * FROM  library.book_user bu where
1=1

    and bu.book_id =#{bookId,jdbcType=INTEGER}


    and bu.book_name  like CONCAT('%',#{bookName},'%')


    and bu.book_type_id =#{bookTypeId,jdbcType=INTEGER}



    and bu.book_author =#{bookAuthor,jdbcType=VARCHAR}


    and bu.ISBN =#{isbn,jdbcType=VARCHAR}


    and bu.book_author =#{bookAuthor,jdbcType=VARCHAR}


    and bu.is_valid =#{isValid,jdbcType=VARCHAR}

select * FROM  library.book_user b
WHERE 1=1

    and b.userId=#{userId,jdbcType=INTEGER}


    and b.user_account=#{userName,jdbcType=VARCHAR}



and  b.user_password=#{password,jdbcType=VARCHAR}
